public class TwosMultiple {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] mul = new int[10];
		
		//System.out.println(mul.length);
		
		for(int i=0;i<=mul.length-1;i++)
		{
			if(i%2==0)
			{   int j=0;
				mul[j]=i;
				System.out.println(mul[j]);
				//j++;
				
			}
		}
		

	}

}
